mapper.select:
SELECT q.id, q.knowledge_point_id, q.question_type, q.question_title, q.question_title_fr, q.question_content, q.question_content_fr, q.options, q.options_fr, q.correct_answer, q.correct_answer_fr, q.answer_explanation, q.answer_explanation_fr, q.difficulty_level, q.points, q.sort_order, q.create_at, q.create_by, q.update_at, q.update_by, q.delete_flag FROM question q INNER JOIN knowledge_point kp ON q.knowledge_point_id = kp.id WHERE kp.grade_id = #{gradeId} AND q.delete_flag = 'N' AND kp.delete_flag = 'N'
AND kp.category_id IN
#{categoryId}
AND q.difficulty_level = #{difficultyLevel}
ORDER BY RAND()

SELECT
FROM question WHERE delete_flag = 'N'
AND knowledge_point_id IN
#{knowledgePointId}
AND difficulty_level = #{difficultyLevel}
ORDER BY RAND()
